#!/bin/bash
# Find TODOs, FIXMEs, and XXXs in the codebase
# Usage: ./find_todos.sh [directory]

TARGET_DIR=${1:-.}

grep -rnE "TODO|FIXME|XXX" "$TARGET_DIR" --exclude-dir={node_modules,.git}

#!/bin/bash
# Find files longer than a certain number of lines
# Usage: ./check_complexity.sh [max_lines] [directory]

MAX_LINES=${1:-300}
TARGET_DIR=${2:-.}

find "$TARGET_DIR" -type f -not -path '*/.*' -not -path '*/node_modules/*' -exec wc -l {} + | sort -nr | awk -v max="$MAX_LINES" '$1 > max {print $0}'

#!/bin/bash
# Find duplicate lines across files (vaguely detects copy-paste)
# Usage: ./find_duplicates.sh [min_lines]

MIN_LINES=${1:-5}

# This is a very rough approximation using find, sort, and uniq
find . -type f -not -path '*/.*' -not -path '*/node_modules/*' -exec cat {} + | sort | uniq -c | sort -nr | awk -v min="$MIN_LINES" '$1 >= min {print $0}' | head -n 20

#!/bin/bash
# Generate a basic test scaffold for a given file
# Usage: ./scaffold_test.sh <source_file>

FILE=$1
if [ ! -f "$FILE" ]; then
  echo "File not found: $FILE"
  exit 1
fi

EXTENSION="${FILE##*.}"
BASENAME=$(basename "$FILE")
FILENAME="${BASENAME%.*}"

case "$EXTENSION" in
  js|ts)
    cat <<EOF
import { describe, it, expect } from 'vitest';
import * as target from './$BASENAME';

describe('$FILENAME', () => {
  it('should be defined', () => {
    expect(target).toBeDefined();
  });
});
EOF
    ;;
  py)
    cat <<EOF
import unittest
import $FILENAME

class Test${FILENAME^}(unittest.TestCase):
    def test_exists(self):
        self.assertIsNotNone($FILENAME)

if __name__ == '__main__':
    unittest.main()
EOF
    ;;
  *)
    echo "Unsupported extension: $EXTENSION"
    ;;
esac
